// DATASET: classrooms from ./datasets/classrooms
const classPrompts = {
  feClassrooms() {
    // Create an array of just the front-end classrooms. e.g.
    // [
    //   { roomLetter: 'A', program: 'FE', capacity: 32 },
    //   { roomLetter: 'C', program: 'FE', capacity: 27 },
    //   { roomLetter: 'E', program: 'FE', capacity: 22 },
    //   { roomLetter: 'G', program: 'FE', capacity: 29 }
    // ]

    let FEE = classrooms.filter(cohort => {
      return cohort.program === 'FE';
    });
    

    const result = FEE;
    return result;

    // Annotation:
    // Write your annotation here as a comment
  },

  totalCapacities() {
    // Create an object where the keys are 'feCapacity' and 'beCapacity',
    // and the values are the total capacity for all classrooms in each program e.g.
    // {
    //   feCapacity: 110,
    //   beCapacity: 96
    // }



    const result = () => {
      let fecap = 0;
      let becap = 0;
      return classrooms.reduce((acc, cur) => {
        if (!acc.feCapacity) {
          acc.feCapacity = 0;
        }
        if(cur.program === 'FE') {
          fecap += cur.capacity;
          acc.feCapacity = fecap;
        }
        if (!acc.beCapacity) {
          acc.beCapacity = 0;
        }
        if(cur.program === 'BE') {
          becap += cur.capacity;
          acc.beCapacity = becap;
        }
        return acc;
      }, {});
    };
    return result();

    // Annotation:
    // Write your annotation here as a comment
  },

  sortByCapacity() {
    // Return the array of classrooms sorted by their capacity (least capacity to greatest)

    let sortByCap = classrooms.sort ((a, b) => {
      return a.capacity - b.capacity;
    });

    const result = sortByCap;
    return result;

    // Annotation: use sort method to return in ascending order
    // Write your annotation here as a comment
  }
};



